import React, { useEffect, useState } from 'react';
import { getCart, processOrder, removeFromCart } from '../../utilities/databaseManager';
import Cart from '../Cart/Cart';
import ReviewItem from '../ReviewItem/ReviewItem';
import fakeData from './../../fakeData/index';
import happyImage from "../../images/giphy.gif";


const Review = () => {
    const [cart, setCart]=useState([]);
    const [orderPlace, setOrderPlace]= useState(false);
    const handleProceedCheckoutr= () => {
        setCart([]);
        setOrderPlace(true);
        processOrder();
    }
    const removeProduct= (productKey)=>{
        console.log('remove clicked', productKey);
        const newCart = cart.filter(pd=>pd.key !== productKey);
        setCart(newCart);
        removeFromCart(productKey);
    }
    useEffect(()=>{
        //cart
        const savedCart = getCart(); //instead of getDatabaseCart
        const productKeys= Object.keys(savedCart);
         const cartProducts = productKeys.map(key => {
             const product= fakeData.find(pd=>pd.key === key);
             product.quantity =savedCart[key];
             return product; 
            
            });
             setCart(cartProducts);
    },[])
    let thankYou ;
    if(orderPlace){
        thankYou= <img src={happyImage}></img>
    }
     

    return (
        <div className='shop-container'>
            <div className='product-container'>
            {
               cart.map(pd => <ReviewItem 
                key={pd.key}
                removeProduct={removeProduct}
                product={pd}></ReviewItem> )
                
            }
            {
                thankYou
            }
        </div>
        <div className='cart-container'>
            <Cart cart={cart}>
            <button onClick={handleProceedCheckout} className="main-button"> Place Order</button>
            </Cart>

        </div>
        
        
        </div>
        
    );
};

export default Review;
