import { useContext, useState } from "react";
import { userContext } from './../../App';
import { useNavigate , useLocation} from "react-router-dom";
import { initializeLoginFrame, handleSignIn, handleSignOut, 
  createWithEmailAndPassword, signInUserWithEmailAndPassword } from './LoginManager';



function Login() {

  initializeLoginFrame();
  const navigate = useNavigate();
  const location = useLocation();
  let from = location.state?.from?.pathname || "/";

  const [loggedInUser, setLoggedInUser] = useContext(userContext);
  const [newUser, setNewUser] = useState(false);
  const [user, setUser] = useState({
    isSignedIn: false,
    photo: "",
    name: "",
    email: "",
    password: "",
    error: "",
    success: false,
  });
const googleSignIn = () => {
    handleSignIn()
    .then(res =>{
        setUser(res);
        setLoggedInUser(res);
        navigate(location.state.from);
  
    } )
};
const googleSignOut = () => {
    handleSignOut()
    .then(res => {
        setUser(res);
        setLoggedInUser(res);
      })
};
  
  

const handleSubmit = (e) => {
  if (newUser && user.email && user.password) {
    createWithEmailAndPassword(user.name, user.email, user.password)
    .then(res =>{
      setUser(res);
      setLoggedInUser(res);
      navigate(location.state.from);

  } )

  }
  if (!newUser && user.email && user.password) {
    signInUserWithEmailAndPassword(user.email, user.password)
    .then(res =>{
      // setUser(res);
      // setLoggedInUser(res);
      // navigate(location.state.from);

  } )
  }
  
    e.preventDefault();
};

  const handleResponse = (res, redirect) => {
      setUser(res);
      setLoggedInUser(res);
      navigate(location.state.from);
}
  const handleChange = (e) => {
    let isFormValid = true;
    if (e.target.name === "email") {
      const isEmailValid = /\S+@\S+\.\S+/.test(e.target.value);
      isFormValid = isEmailValid;
    }
    if (e.target.name === "password") {
      const isPasswordValid =
        e.target.value.length > 6 && e.target.value.length < 32;
      const passwordHasNumber = /\d{1}/.test(e.target.value);
      isFormValid = isPasswordValid && passwordHasNumber;
    }
    if (isFormValid) {
      const newUserInfo = { ...user };
      newUserInfo[e.target.name] = e.target.value;
      setUser(newUserInfo);
    }
  };

  return (

    <div style={{textAlign:'center'}} >
      {user.isSignedIn ? (
        <button onClick={googleSignOut}>Sign out</button>
      ) : (
        <button
          style={{ background: "#2da44e", color: "#ffffff" }}
          onClick={googleSignIn}
        >
          Sign in with google
        </button>
      )}
      {user.isSignedIn && (
        <div>
          <h1>Welcome {user.name} </h1>
          <p> Your email: {user.email} </p>
        </div>
      )}
      <h1>Our own authentication</h1>
      <input
        type="checkbox"
        onChange={() => setNewUser(!newUser)}
        name="newUser"
      />
      <label htmlFor="newUser">New user sign up</label>
      <form action="" onSubmit={handleSubmit}>
        {newUser && (
          <input
            type="text"
            name="name"
            placeholder="Your Name"
            onBlur={handleChange}
          />
        )}
        <br />
        <input
          type="email"
          name="email"
          id="input-email"
          placeholder="Enter your email address"
          onBlur={handleChange}
          required
        />

        <br />
        <input
          type="password"
          name="password"
          id="password"
          placeholder="Enter your password"
          onBlur={handleChange}
          required
        />
        <br />
        <input
          style={{ background: "#2da44e", color: "#ffffff" }}
          type="submit"
          value={newUser ? "Sign up" : "Sign in"}
        />
      </form>
      {user.success && (
        <p style={{ color: "green" }}>
          User account {newUser ? "created" : "logged in"} successfully
        </p>
      )}
      {user.error && <p style={{ color: "red" }}>{user.error}</p>}
    </div>
  );
  
};


export default Login;
