import React, { useEffect, useState } from 'react';

import "./Shop.css";
import Product from '../Product/Product';
import Cart from '../Cart/Cart';
import { addToCart, getCart } from '../../utilities/databaseManager';
import { Link } from 'react-router-dom';



const Shop = () => {
    //const first10 = fakeData.slice(0,10);
    const [products, setProducts] = useState([]);
    const [cart, setCart] = useState([]);
    const [search, setSearch] = useState('');
    document.title='Shop more';

    useEffect(() => {
       fetch('https://serene-wave-85583.herokuapp.com/products?search=' + search)
       .then(res => res.json())
       .then(data => setProducts(data))
    }, [search]);
    
    useEffect(()=>{
        const savedCart = getCart();
        const productKeys= Object.keys(savedCart);
        fetch('https://serene-wave-85583.herokuapp.com/productByKeys', {
            method: 'POST',
            headers: {
                'Content-Type':'application/json'
            },
            body: JSON.stringify(productKeys)
        })
        .then(res => res.json())
        .then(data => setCart(data))
    },[])
    const handleSearch= event => {
        setSearch(event.target.value)
    }
    const handleAddProduct = (product) => {
        const toBeAddedKey = product.key;
        const sameProduct = cart.find(pd => pd.key === toBeAddedKey);

        let count =1 ;
        let newCart;
        if(sameProduct){
            count = sameProduct.quantity + 1;
            sameProduct.quantity = count ;
            const others = cart.filter(pd=>pd.key !== toBeAddedKey);
            newCart=[...others, sameProduct];
        }
        else{
            product.quantity = 1 ;
             newCart = [...cart , product];
        }
        setCart(newCart);
        
        
        addToCart(product.key, count);
        
    }
    
    return (
    
        <div class='shop-container' >
            <div className="product-container">
            <input type='text' className='product-search' onBlur={handleSearch}  placeholder='Search here' />
                {
                    products.length === 0 && <p>loading...</p>
                }
            
            {
                products.map(pd => <Product
                key={pd.key}
                showAddToCart={true}
                    handleAddProduct = {handleAddProduct}
                product={pd} > 
                
                </Product>)
            }
            
            </div>
            <div className="cart-container">
            <Cart cart={cart} >
            <Link to="/review"> 
            <button className="main-button">Review Order</button></Link>
            </Cart>

            </div>
            
        </div>

    );
};

export default Shop;
