import React, { useContext } from "react";
import { useForm } from "react-hook-form";
import { UserContext } from "../../App";
import { getCart, processOrder } from "../../utilities/databaseManager";
import "./Shipment.css";
import ProcessPayment from './../ProcessPayment/ProcessPayment';

const Shipment = () => {
  const {
    register,
    handleSubmit,
    watch,
    formState: { errors },
  } = useForm();
  const [loggedInUser, setLoggedInUser] = useContext(UserContext);
  const [shippingData, setShippingData] = useState (null);

  const onSubmit = (data) => {
    setShippingData(data);
  };
  const handlePaymentSuccess = paymentId =>{
    const savedCart = getCart();
    const orderDetails = {
      ...loggedInUser,
      products: savedCart,
      shipment: data,
      paymentId,
      orderTime: new Date(),
    };

    fetch("https://serene-wave-85583.herokuapp.com/addOrder", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(orderDetails),
    })
      .then((res) => res.json())
      .then((data) => {
        if (data) {
          processOrder();
          alert("Your order place success");
        }
      });
  }

  return (
    <div className="row">
      <div style={{ display: shippingData ? 'none': 'block' }} className="col-md-6">
        <form className="sh ip-form" onSubmit={handleSubmit(onSubmit)}>
          <input
            defaultValue={loggedInUser.name}
            {...register("name", { required: true })}
            placeholder="Your name"
          />
          {errors.name && <span className="error">Name is required</span>}

          <input
            defaultValue={loggedInUser.email}
            {...register("email", { required: true })}
            placeholder="Your email"
          />
          {errors.email && <span className="error">Email is required</span>}

          <input
            {...register("address", { required: true })}
            placeholder="Your address"
          />
          {errors.address && <span className="error">Address is required</span>}

          <input
            {...register("phone", { required: true })}
            placeholder="Your phone number"
          />
          {errors.phone && (
            <span className="error">Phone number is required</span>
          )}
          <input type="submit" />
        </form>
      </div>
      <div style={{ display : shippingData ? 'block': 'none' }} className="col-md-6">
            <h2>Please pay for me</h2>
        <ProcessPayment></ProcessPayment>
      </div>
    </div>
  );
};

export default Shipment;
