    for(let i=0; i<cart.length; i++){
        const product = cart[i];
        total = total + product.price;
    }
    let shipping = 12.99;
    if (total >35) {
    shipping = 0 ;        
    }
    else 
    return (
        <div>
            <h4>Order Summery</h4>
            <p>Items Ordered: {cart.length} </p>
            <p>Total Price:{total}</p>
        </div>
    );
};

export default Cart;
